import React from "react";
import { Container, Row, Col, Form } from "react-bootstrap";
import Job from "./Job";
import { connect } from "react-redux";
import { setJobs } from "../store/actions"; // Importa le azioni necessarie

const MainSearch = ({ jobs, setJobs }) => {
  const baseEndpoint = "https://strive-benchmark.herokuapp.com/api/jobs?search=";

  const handleChange = (e) => {
    setJobs([]); // Pulisci l'elenco dei lavori quando cambia la query
    // Aggiorna la query usando setJobs se necessario
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(baseEndpoint + e.target.elements.search.value + "&limit=20");
      if (response.ok) {
        const { data } = await response.json();
        setJobs(data);
      } else {
        alert("Error fetching results");
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Container>
      <Row>
        <Col xs={10} className="mx-auto my-3">
          <h1 className="display-1">Remote Jobs Search</h1>
        </Col>
        <Col xs={10} className="mx-auto">
          <Form onSubmit={handleSubmit}>
            <Form.Control type="search" name="search" placeholder="type and press Enter" onChange={handleChange} />
          </Form>
        </Col>
        <Col xs={10} className="mx-auto mb-5">
          {jobs.map((jobData) => (
            <Job key={jobData._id} data={jobData} />
          ))}
        </Col>
      </Row>
    </Container>
  );
};

const mapStateToProps = (state) => ({
  jobs: state.jobs,
});

const mapDispatchToProps = {
  setJobs,
};

export default connect(mapStateToProps, mapDispatchToProps)(MainSearch);
