// components/FavoritesPage.jsx
import React from "react";
import { connect } from "react-redux";
import { Container, Row, Col, Card, Button } from "react-bootstrap";

const FavoritesPage = ({ favorites }) => {
  return (
    <Container>
      <Row>
        <Col xs={12} className="my-3">
          <h1 className="display-1">Favorite Companies</h1>
        </Col>
        <Col xs={12} className="my-3">
          {favorites.length > 0 ? (
            // Visualizza l'elenco dei preferiti
            favorites.map((companyName) => (
              <Card key={companyName} className="mb-3">
                <Card.Body>
                  <Card.Title>{companyName}</Card.Title>
                  {/* Aggiungi ulteriori dettagli qui, se necessario */}
                  <Button variant="danger">Remove from Favorites</Button>
                </Card.Body>
              </Card>
            ))
          ) : (
            <p>Nessuna azienda nei preferiti</p>
          )}
        </Col>
      </Row>
    </Container>
  );
};

const mapStateToProps = (state) => ({
  favorites: state.favorites,
});

export default connect(mapStateToProps)(FavoritesPage);
