// store/actions.jsx
export const setJobs = (jobs) => ({
    type: 'SET_JOBS',
    payload: jobs,
  });
  
  export const setCompanyJobs = (companyJobs) => ({
    type: 'SET_COMPANY_JOBS',
    payload: companyJobs,
  });
  
  export const addToFavorites = (Name) => ({
    type: 'ADD_TO_FAVORITES',
    payload: name,
  });
  
  export const removeFromFavorites = (Name) => ({
    type: 'REMOVE_FROM_FAVORITES',
    payload: name,
  });
